mapper.select:
select qnano, commentid, userid, content, regtime
from comment 
where qnano = #{qnano} order by commentid desc;

select qnano, commentid, userid, content, regtime
from comment 
where commentid = #{commentid}

mapper.insert:
insert into comment(
	userid, content, qnano
) values (
	#{userid}, #{content}, #{qnano}
)

mapper.update:
update comment 
   set content = #{content}
 where commentid = #{commentid}
