mapper.select:
select if(count(*) = 0,0,avg(rating)) as avgRate 
from review
where aptcode = #{aptcode}

select count(no)
from review
where aptcode = #{aptcode}

select no, content, rating, regtime, userid, aptcode
from review
where no = #{no}

mapper.insert:
insert into review(
 content,rating, regtime, userid, aptcode
) values (
	#{content}, #{rating}, now(), #{userid}, #{aptcode}
)

mapper.update:
update review 
   set content = #{content}, 
   	   rating = #{rating}
 where no = #{no}
