mapper.select:
select if(count(*) = 0,0,avg(rating)) as avgRate 
from sreview
where scode = #{scode}

select count(no)
from sreview
where scode = #{scode}

select no, content, rating, regtime, userid, scode
from sreview
where no = #{no}

mapper.insert:
insert into sreview(
 content,rating, regtime, userid, scode
) values (
	#{content}, #{rating}, now(), #{userid}, #{scode}
)

mapper.update:
update sreview 
   set content = #{content}, 
   	   rating = #{rating}
 where no = #{no}
